package Udemytrilas;

public class SwitchStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String A = "8";

		switch (A) {
		case "1":
			System.out.println("apple print");
			break;

		case "2":
			System.out.println("bowl print");
			break;
			
		case "3":
			System.out.println("cat print");
			break;

		case "4":
			System.out.println("Dog print");
			break;
		case "5":
			System.out.println("elephant print");
			break;
		default:
			System.out.println("no statement found");
			break;

		}

	}
}
